import walmartLogo from '../assets/images/Walmart.png';
import store from '../assets/images/store.png';
import Name from '../assets/images/Name.png';
import VisionXR_Logo from '../assets/images/VisionXR_Logo.png';
import Logo_black from '../assets/images/Logo_black.png';
import { Link } from "react-router-dom";
import { Icon } from '@iconify/react';
import TextInput from '../components/TextInput.js';


const FormPage = () => {
    return (
        <div className="w-full h-full bg-gradient-to-b from-lightblue to-deepblue flex">
            {/* left side */}
            <div className='w-1/2'>
                <div className='m-12 font-black text-5xl text-yelloe'>
                    ApeXR
                </div>
                <div className='mt-28 ml-8'>
                    <img src={store} alt="logoBig" />
                </div>
            </div>
            {/* right side */}
            <div className=' w-1/2'>
                <div className='inputRegion w-1/3 py-10 flex items-center justify-center flex-col'>
                    <div className='font-bold mb-4'>Log in to continue.</div>
                    <TextInput
                        label='Email address'
                        placeholder='Email address'
                        className='my-6'
                        value={email}
                        setValue={setEmail}
                    />
                    <PasswordInput
                        label='Password'
                        placeholder='Password'
                        value={password}
                        setValue={setPassword}
                    />
                    <div className=' w-full flex items-center justify-end my-8'>
                        <button
                            className='h-2/3 flex items-center justify-center rounded-full font-semibold bg-gradient-to-r from-red-400 to-pink-600 p-3 px-10 mt-6'
                            onClick={(e) => {
                                e.preventDefault();
                                login();
                            }}>
                            LOG IN
                        </button>
                    </div>
                    <div className='w-full border border-solid border-gray-300'></div>
                    <div className='my-6 font-semibold text-lg'>Don't have an account?</div>
                    <Link
                        to='/signup'
                        className='border border-gray-500 text-gray-500 w-full flex items-center justify-center py-4 rounded-full font-bold'>
                        <div>SIGN UP</div>
                    </Link>
                </div>
            </div>
        </div>
    )
};

export default FormPage;
